import React, { useEffect } from 'react';
import { gsap, Draggable } from 'gsap/all';
import Axios from 'axios';
gsap.registerPlugin(Draggable);

// 處理每個家俱的屬性
function Furniture({
   furn_id = 'duck',
   roomInteriorFurnDis = 'none',
   x = 0, y = 0,
   updatePage = false,
   setUpdatePage = f => f
}) {

   const onDragEndHandler = async e => {
      if (e.hitTest('#dropArea')) {
         await Axios.put('https://still-scrubland-65207.herokuapp.com/acc_furn/takeBack', {
            acc_email: localStorage.getItem("loginState"),
            furn_id: furn_id
         });
      } else {
         await Axios.put('https://still-scrubland-65207.herokuapp.com/gsap/updatePos', {
            acc_email: localStorage.getItem("loginState"),
            furn_id: furn_id,
            x: e.x,
            y: e.y
         });
      }
      gsap.set(`#${furn_id}`, { x: e.x, y: e.y });
      gsap.set(`#dropArea`, { display: 'none' });
      setUpdatePage(updatePage => !updatePage);
   };

   useEffect(() => {
      if (furn_id != 'duck') {
         Draggable.create(`#${furn_id}`, {
            bounds: document.getElementById('container'),
            inertia: true,
            onDragStart: function () {
               gsap.set(`#dropArea`, { display: 'block' });
            },
            onDragEnd: function () { onDragEndHandler(this) },
         });
         gsap.set(`#${furn_id}`, { x: x, y: y });
      }
   }, []);

   return (
      <React.Fragment>
         {renderSwitch(furn_id, roomInteriorFurnDis)}
      </React.Fragment>
   );
}

// 回傳首頁的家俱
export const HomepageFurniture = () => {
   const furnList = ['duck', 'basketball', 'bathTube', 'cabinet', 'clock',
      'femaleDuck', 'glasses', 'light', 'mirror', 'protrait', 'TV', 'weight'];

   const outputFurniture = () => {
      return furnList[0] && furnList.map(val =>
         renderSwitch(val, 'block')
      )
   };

   return (
      <React.Fragment>
         {outputFurniture()}
      </React.Fragment>
   );
}

